std::vector<Enemy> addEnemies(int level) {
  std::vector<Enemy> enemies = vector<Enemy>();
  switch (level) {
    case 1:
      enemies.push_back(Enemy(20, 30));
      enemies.push_back(Enemy(12, 60));
      enemies.push_back(Enemy(1, 20));
      enemies.push_back(Enemy(11, 1));
      enemies.push_back(Enemy(5, 50));
      enemies.push_back(Enemy(16, 12));
      break;
    default:
      throw std::invalid_argument("No Area for the given level");
  }
  return enemies;
} 

Area getArea(int level) {
  switch (level) {
    case 1:
      return A3;
    default:
      throw std::invalid_argument("No Area for the given level");
  }
}


